import MyInput from "components/UI/input/MyInput";
import React from "react";

const PhoneFilter = ({ filter, setFilter }) => {
  return (
    <form>
      <MyInput
        placeholder="search..."
        type="text"
        value={filter}
        onChange={(e) => {
          setFilter(e.target.value);
        }}
      />
    </form>
  );
};

export default PhoneFilter;
