import MyButton from 'components/UI/button/MyButton';
import MyInput from 'components/UI/input/MyInput';
import React, { useState } from 'react';
import s from "./PhoneForm.module.css";

const PhoneForm = ({createContact}) => {

    const [contact, setContact] = useState({name : "", number : ""});

    const toAddContact = (e) => {
        e.preventDefault();
        const newContact = {
            ...contact, id: Date.now()
        };
        createContact(newContact);
        // console.log(newContact)
        setContact({name : "", number : ""});
    }

    return (
            <form className={s.myForm} onSubmit={toAddContact}>
                <MyInput required type="text" placeholder="name" name="name" value={contact.name} onChange={e => setContact({...contact, name : e.target.value})}/>
                <MyInput required type="text" placeholder="number" name="number" value={contact.number} onChange={e => setContact({...contact, number : e.target.value})}/>
                <MyButton type="submit">Add contact</MyButton>
            </form>
    );
};

export default PhoneForm;
