import React, { useState, useEffect } from "react";
import Header from "./header/Header";
import PhoneForm from "./phoneForm/PhoneForm";
import toast, {Toaster} from "react-hot-toast";
import ContactsList from "./contactsList/ContactsList";
import PhoneFilter from "./phoneFilter/PhoneFilter";

export const App = () => {
  const [contacts, setContacts] = useState([
    { id: "id-1", name: "Rosie Simpson", number: "459-12-56" },
    { id: "id-2", name: "Hermione Kline", number: "443-89-12" },
    { id: "id-3", name: "Eden Clements", number: "645-17-79" },
    { id: "id-4", name: "Annie Copeland", number: "227-91-26" },
  ]);

  const [filter, setFilter] = useState("");

  useEffect(() => {
    const currentContacts = JSON.parse(localStorage.getItem("contacts"));
    if (currentContacts) {
      setContacts(currentContacts);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("contacts", JSON.stringify(contacts));
  }, [contacts]);

  const toCreateContact = (newContact) => {
    setContacts([...contacts, newContact]);
    toast.success("Created new contact!");
  };

  const toDeleteContact = (id) => {
    setContacts(contacts.filter(contact => contact.id !== id));
    toast.success("Deleted contact!");
  }

  const toFindContact = () => {
    return contacts.filter(contact => contact.name.toLowerCase().includes(filter.toLowerCase()) || contact.number.includes(filter));
  }
  const newArrayContacts = toFindContact();
  // console.log(newArrayContacts);

  return (
    <div style={{width:"500px", margin:"auto"}}>
      <Toaster position="top-center" reverseOrder={false} />
      <Header props="Phone book" />
      <PhoneForm createContact={toCreateContact} />
      <PhoneFilter filter={filter} setFilter={setFilter}/>
      {newArrayContacts ?
      <ContactsList contacts={newArrayContacts} deleteContact={toDeleteContact}/>
      :<p>no contacts found</p>
    }

    </div>
  );
};
