const PhoneFilter = () => {

  const filter = useSelector(getFilter)
  const dispatch = useDispatch();

  return (
    <form>
      <MyInput
        placeholder="search..."
        type="text"
        value={filter}
        // onChange={(e) => dispatch(addFilter(e.target.value))}
        onChange={e => dispatch(addFilter(e.target.value))}
      />
    </form>
  );
};

export default PhoneFilter;
