import MyButton from 'components/UI/button/MyButton';
import MyInput from 'components/UI/input/MyInput';
import React, { useState } from 'react';

const PhoneForm = () => {

    const [contact, setContact] = useState({name : "", number : ""});

    handleChange = (event) => {
        setContact({...contact, name : event.target.value, number : event.target.value})
    }

    return (
        <div>
            My form
            <form>
                <MyInput type="text" placeholder="name" name="name" value={contact.name} onChange={handleChange}/>
                <MyInput type="text" placeholder="number" name="namber" value={contact.number} onChange={handleChange}/>
                <MyButton>Add contact</MyButton>
            </form>
        </div>
    );
};

export default PhoneForm;
