import { createReducer } from "@reduxjs/toolkit";
import { addContact, deleteContact, filterContacts } from "./actions";

const initialState = {
  contacts: [
    { id: "id-1", name: "Rosie Simpson", number: "459-12-56" },
    { id: "id-2", name: "Hermione Kline", number: "443-89-12" },
    { id: "id-3", name: "Eden Clements", number: "645-17-79" },
    { id: "id-4", name: "Annie Copeland", number: "227-91-26" },
  ],
  filter: ""
};

const reducer = createReducer(initialState, (builder) => {
  builder
    .addCase(addContact, (state, action) => {
      const { id, name, number } = action.payload;
      state.contacts.push({ id, name, number });
    })
    .addCase(deleteContact, (state, action) => {
      const { id } = action.payload;
      const index = state.contacts.findIndex((contact) => contact.id === id);
      state.contacts.splice(index);
    })
    .addCase(filterContacts, (state, action) => {
        const {filter} = action.payload;
        state.contacts.filter(contact =>contact.name.toLowerCase().includes(filter.toLowerCase()) || contact.number.includes(filter));
    })
});

export default reducer;
